    private void btnCadastrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCadastrarActionPerformed
        // Botão cadastrar
        Produtos obj = new Produtos();

        obj.setCod_sigma(TxtCodSigma.getText());
        obj.setCod_jde(TxtCodJDE.getText());
        obj.setItem(TxtItem.getText());
        obj.setUnidade_medida(cbUnMedida.getSelectedItem().toString());
        obj.setSigla_unidade_medida(cbSiglaUn.getSelectedItem().toString());
        obj.setModalidade_abastecimento(cbModalidade.getSelectedItem().toString());
        obj.setGrande_grupo(cbGrandeGrupo.getSelectedItem().toString());
        obj.setGrupo(cbGrupo.getSelectedItem().toString());
        obj.setSubgrupo(cbSubGrupo.getSelectedItem().toString());


        ProdutosDAO dao = new ProdutosDAO();
        dao.cadastrarProduto(obj);

        new Utilitarios().LimpaTela(jPanel2);
    }//GEN-LAST:event_btnCadastrarActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // Botão editar:

        Produtos obj = new Produtos();

        obj.setCod_sigma(TxtCodSigma.getText());
        obj.setCod_jde(TxtCodJDE.getText());
        obj.setItem(TxtItem.getText());
        obj.setUnidade_medida(cbUnMedida.getSelectedItem().toString());
        obj.setSigla_unidade_medida(cbSiglaUn.getSelectedItem().toString());
        obj.setModalidade_abastecimento(cbModalidade.getSelectedItem().toString());
        obj.setGrande_grupo(cbGrandeGrupo.getSelectedItem().toString());
        obj.setGrupo(cbGrupo.getSelectedItem().toString());
        obj.setSubgrupo(cbSubGrupo.getSelectedItem().toString());


        obj.setId(Integer.parseInt(TxtId.getText()));


        ProdutosDAO dao = new ProdutosDAO();
        dao.aleterarProduto(obj);
        new Utilitarios().LimpaTela(jPanel2);
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // Botao excluir

        Produtos obj = new Produtos();

        obj.setId(Integer.parseInt(TxtId.getText()));

        ProdutosDAO dao = new ProdutosDAO();
        dao.excluirProdutos(obj);
        new Utilitarios().LimpaTela(jPanel2);
    }//GEN-LAST:event_jButton1ActionPerformed

